  var pauseFlag = false;
function onPlayerReady(event) {
// do nothing, no tracking needed
}
function onPlayerStateChange(event) {
// track when user clicks to Play
if (event.data == YT.PlayerState.PLAYING) {
  ga('send', 'event', 'Product Page', 'YT-Video-Play', 'Product ID 196');
pauseFlag = true;
}
// track when user clicks to Pause
if (event.data == YT.PlayerState.PAUSED &amp;&amp; pauseFlag) {
  ga('send', 'event', 'Product Page', 'YT-Video-Pause', 'Product ID 196');
pauseFlag = false;
}
// track when video ends
if (event.data == YT.PlayerState.ENDED) {
  ga('send', 'event', 'Product Page', 'YT-Video-Finished', 'Product ID 196');
}
}
